package com.geekster.homework;

public class TypeCast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 45;
		float b = a;
		double z = b;
		
		System.out.println("This is Integer Value of a" +"  " + a);
		
		System.out.println("This is Value of a after typecast into Float" +"  " + b);
		
		System.out.println("This is Double Value of b" +"  " + z);
		
		
	}


}
